pub(crate) fn get_quote(db: &mut PickleDb) -> String {
    if let Some(quote) = db.get::<usize>("quote") {
        match quote {
            0..=999 => {
                let new_quote = quote + 1;
                let _ = db.set("quote", &new_quote);
                QUOTES[new_quote].to_string()
            }
            _ => {
                let new_quote = 0;
                let _ = db.set("quote", &new_quote);
                QUOTES[new_quote].to_string()
            }
        }
    } else {
        let _ = db.set("quote", &0);
        QUOTES[0].to_string()
    }
}
